     1import { useState, useEffect } from 'preact/hooks'
     2import { Header } from './Header'
     3import { DailyProgress } from './DailyProgress'
     4import { EntryList } from './EntryList'
     5import { EntryForm } from './EntryForm'
     6import { Settings } from './Settings'
     7import { Modal } from './Modal'
     8import { FAB } from './FAB'
     9import { DatePicker } from './DatePicker'
    10import { ProfileSettings } from './ProfileSettings'
    11import { getEntriesByDateAndProfile, addEntry, updateEntry, deleteEntry } from '../utils/db'
    12import { getGoals, saveGoals, getProfiles, getActiveProfileId, setActiveProfileId, needsMigration } from '../utils/storage'
    13import { getToday } from '../utils/date'
    14
    15export function App() {
    16  const [currentDate, setCurrentDate] = useState(getToday())
    17  const [entries, setEntries] = useState([])
    18  const [activeProfileId, setActiveProfile] = useState(getActiveProfileId())
    19  const [goals, setGoals] = useState(getGoals(activeProfileId))
    20  const [showForm, setShowForm] = useState(false)
    21  const [showSettings, setShowSettings] = useState(false)
    22  const [showCalendar, setShowCalendar] = useState(false)
    23  const [showProfilePicker, setShowProfilePicker] = useState(false)
    24  const [showMigrationPrompt, setShowMigrationPrompt] = useState(false)
    25  const [editingEntry, setEditingEntry] = useState(null)
    26  const [deletingEntry, setDeletingEntry] = useState(null)
    27
    28  const profiles = getProfiles()
    29  const hasProfiles = profiles.length > 0
    30  const currentProfile = profiles.find(p => p.id === activeProfileId)
    31
    32  useEffect(() => {
    33    if (hasProfiles && !activeProfileId) {
    34      setShowProfilePicker(true)
    35    }
    36  }, [hasProfiles, activeProfileId])
    37
    38  useEffect(() => {
    39    if (needsMigration()) {
    40      setShowMigrationPrompt(true)
    41    }
    42  }, [])
    43
    44  useEffect(() => {
    45    loadEntries()
    46  }, [currentDate, activeProfileId])
    47
    48  useEffect(() => {
    49    setGoals(getGoals(activeProfileId))
    50  }, [activeProfileId])
    51
    52  async function loadEntries() {
    53    const data = await getEntriesByDateAndProfile(currentDate, activeProfileId)
    54    setEntries(data)
    55  }
    56
    57  async function handleSaveEntry(entryData) {
    58    if (entryData.id) {
    59      await updateEntry(entryData)
    60    } else {
    61      await addEntry({ ...entryData, date: currentDate, profileId: activeProfileId })
    62    }
    63    await loadEntries()
    64    setShowForm(false)
    65    setEditingEntry(null)
    66  }
    67
    68  async function handleDeleteEntry() {
    69    if (deletingEntry) {
    70      await deleteEntry(deletingEntry.id)
    71      await loadEntries()
    72      setDeletingEntry(null)
    73    }
    74  }
    75
    76  function handleEditEntry(entry) {
    77    setEditingEntry(entry)
    78    setShowForm(true)
    79  }
    80
    81  function handleSaveGoals(newGoals) {
    82    saveGoals(newGoals, activeProfileId)
    83    setGoals(newGoals)
    84    setShowSettings(false)
    85  }
    86
    87  function handleCloseForm() {
    88    setShowForm(false)
    89    setEditingEntry(null)
    90  }
    91
    92  function handleProfileChange(profileId) {
    93    setActiveProfileId(profileId)
    94    setActiveProfile(profileId)
    95    setShowProfilePicker(false)
    96    setShowSettings(false)
    97  }
    98
    99  function handleProfileClick() {
   100    setActiveProfileId(null)
   101    setActiveProfile(null)
   102    setShowProfilePicker(true)
   103  }
   104
   105  return (
   106    <div class="app">
   107      <Header
   108        currentDate={currentDate}
   109        onDateChange={setCurrentDate}
   110        onSettingsClick={() => setShowSettings(true)}
   111        onCalendarClick={() => setShowCalendar(true)}
   112        profileName={currentProfile?.name}
   113        onProfileClick={handleProfileClick}
   114      />
   115
   116      <DailyProgress entries={entries} goals={goals} />
   117
   118      <EntryList
   119        entries={entries}
   120        onEdit={handleEditEntry}
   121        onDelete={setDeletingEntry}
   122      />
   123
   124      <FAB onClick={() => setShowForm(true)} />
   125
   126      {showForm && (
   127        <EntryForm
   128          entry={editingEntry}
   129          onSave={handleSaveEntry}
   130          onCancel={handleCloseForm}
   131        />
   132      )}
   133
   134      {showSettings && (
   135        <Settings
   136          goals={goals}
   137          onSave={handleSaveGoals}
