/* ============================================
   CALORIE TRACKER  Warm Wellness Theme
   Aesthetic: Kinfolk meets Headspace
   ============================================ */

:root {
  /* Typography */
  --font-display: 'Fraunces', Georgia, serif;
  --font-body: 'Nunito Sans', -apple-system, BlinkMacSystemFont, sans-serif;

  /* Core Palette */
  --color-cream: #FAF7F2;
  --color-white: #FFFFFF;
  --color-sage: #5C6B54;
  --color-sage-light: #8A9A82;
  --color-sage-pale: #E8EDE6;
  --color-sage-faint: #F2F5F0;
  --color-terracotta: #C17B5F;
  --color-terracotta-light: #D4A08A;
  --color-terracotta-pale: #F5EBE6;
  --color-espresso: #3D3D3D;
  --color-warm-gray: #6B6B6B;
  --color-muted: #9B9B9B;

  /* Semantic */
  --color-success: #7D9B76;
  --color-warning: #D4A574;

  /* Macros */
  --color-protein: #8B7355;
  --color-carbs: #C4A35A;
  --color-fat: #B08D7A;

  /* Spacing */
  --space-1: 4px;
  --space-2: 8px;
  --space-3: 12px;
  --space-4: 16px;
  --space-5: 20px;
  --space-6: 24px;
  --space-8: 32px;
  --space-10: 40px;

  /* Borders */
  --radius-sm: 8px;
  --radius-md: 12px;
  --radius-lg: 20px;
  --radius-xl: 28px;
  --radius-full: 9999px;

  /* Shadows */
  --shadow-sm: 0 1px 3px rgba(92,107,84,0.06);
  --shadow-md: 0 4px 16px rgba(92,107,84,0.08);
  --shadow-lg: 0 8px 32px rgba(92,107,84,0.1);
  --shadow-glow: 0 0 40px rgba(193,123,95,0.2);

  /* Motion */
  --duration-fast: 150ms;
  --duration-normal: 250ms;
  --duration-slow: 400ms;
  --ease-out: cubic-bezier(0.16, 1, 0.3, 1);
  --ease-in-out: cubic-bezier(0.65, 0, 0.35, 1);
}

/* ============================================
   BASE RESET
   ============================================ */

* {
  box-sizing: border-box;
  margin: 0;
  padding: 0;
}

body {
  font-family: var(--font-body);
  background: var(--color-cream);
  color: var(--color-espresso);
  line-height: 1.6;
  min-height: 100vh;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
}

/* Subtle background atmosphere */
body::before {
  content: '';
  position: fixed;
  top: -20%;
  right: -10%;
  width: 60%;
  height: 60%;
  background: radial-gradient(ellipse at center, rgba(232,237,230,0.7) 0%, transparent 70%);
  pointer-events: none;
  z-index: -1;
}

body::after {
  content: '';
  position: fixed;
  bottom: -10%;
  left: -10%;
  width: 50%;
  height: 50%;
  background: radial-gradient(ellipse at center, rgba(245,235,230,0.5) 0%, transparent 70%);
  pointer-events: none;
  z-index: -1;
}

#app {
  max-width: 480px;
  margin: 0 auto;
  padding: var(--space-5);
  padding-bottom: 120px;
}

/* ============================================
   HEADER
   ============================================ */

.header {
  margin-bottom: var(--space-6);
}

.header-top {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: var(--space-5);
}

.header-title {
  font-family: var(--font-display);
  font-size: 1.75rem;
  font-weight: 600;
  color: var(--color-sage);
  letter-spacing: -0.02em;
}

.header-profile {
  display: flex;
  align-items: center;
  gap: var(--space-2);
}

.header-profile-name {
  font-size: 0.875rem;
  font-weight: 500;
  color: var(--color-warm-gray);
}

.btn-lock {
  background: none;
  border: none;
  padding: var(--space-1);
  cursor: pointer;
  color: var(--color-muted);
  transition: color var(--duration-fast);
  border-radius: var(--radius-sm);
}

.btn-lock:hover {
  color: var(--color-sage);
}

/* Date Navigation */
.date-nav {
  display: flex;
  align-items: center;
  justify-content: center;
  gap: var(--space-3);
}

.date-display {
  background: var(--color-white);
  border: 1px solid var(--color-sage-pale);
  border-radius: var(--radius-full);
  padding: var(--space-2) var(--space-5);
  font-family: var(--font-body);
  font-size: 0.9375rem;
  font-weight: 600;
  color: var(--color-sage);
  cursor: pointer;
  transition: all var(--duration-fast);
  box-shadow: var(--shadow-sm);
}

.date-display:hover {
  background: var(--color-sage-faint);
  border-color: var(--color-sage-light);
}

.btn-icon {
  background: var(--color-white);
  border: 1px solid var(--color-sage-pale);
  border-radius: var(--radius-full);
  width: 44px;
  height: 44px;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  color: var(--color-sage);
  transition: all var(--duration-fast);
  box-shadow: var(--shadow-sm);
}

.btn-icon:hover:not(:disabled) {
  background: var(--color-sage-faint);
  border-color: var(--color-sage-light);
  transform: scale(1.05);
}

.btn-icon:active:not(:disabled) {
  transform: scale(0.95);
}

.btn-icon:disabled {
  opacity: 0.4;
  cursor: not-allowed;
}

.btn-icon:focus-visible,
.date-display:focus-visible {
  outline: 2px solid var(--color-sage);
  outline-offset: 2px;
}

/* ============================================
   PROGRESS CARD  Hero Element
   ============================================ */

.progress-card {
  background: var(--color-white);
  border-radius: var(--radius-xl);
  padding: var(--space-6);
  box-shadow: var(--shadow-md);
  margin-bottom: var(--space-6);
  position: relative;
  overflow: hidden;
}

/* Subtle decorative gradient */
.progress-card::before {
  content: '';
  position: absolute;
  top: 0;
  right: 0;
  width: 150px;
  height: 150px;
  background: radial-gradient(circle at top right, rgba(232,237,230,0.5) 0%, transparent 70%);
  pointer-events: none;
}

.progress-main {
  display: flex;
  flex-direction: column;
  align-items: center;
  margin-bottom: var(--space-6);
  position: relative;
}

/* Radial Progress Ring */
.progress-ring {
  position: relative;
  width: 180px;
  height: 180px;
  margin-bottom: var(--space-4);
}

.progress-ring svg {
  transform: rotate(-90deg);
  width: 100%;
  height: 100%;
}

.progress-ring-bg {
  fill: none;
  stroke: var(--color-sage-pale);
  stroke-width: 12;
}

.progress-ring-fill {
  fill: none;
  stroke-width: 12;
  stroke-linecap: round;
  transition: stroke-dashoffset var(--duration-slow) var(--ease-out),
              stroke var(--duration-normal);
}

.progress-ring-fill--under {
  stroke: var(--color-sage);
}

.progress-ring-fill--close {
  stroke: var(--color-terracotta);
}

.progress-ring-fill--over {
  stroke: var(--color-warning);
}

.progress-ring-content {
  position: absolute;
  inset: 0;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
}

.progress-value {
  font-family: var(--font-display);
  font-size: 2.75rem;
  font-weight: 700;
  color: var(--color-espresso);
  line-height: 1;
  letter-spacing: -0.02em;
}

.progress-label {
  font-size: 0.875rem;
  font-weight: 500;
  color: var(--color-muted);
  margin-top: var(--space-1);
}

/* Legacy progress bar (kept for fallback) */
.progress-calories {
  display: flex;
  align-items: baseline;
  gap: var(--space-1);
  margin-bottom: var(--space-3);
}

.progress-bar-container {
  height: 10px;
  background: var(--color-sage-pale);
  border-radius: var(--radius-full);
  overflow: hidden;
  width: 100%;
}

.progress-bar-container--sm {
  height: 6px;
}

.progress-bar {
  height: 100%;
  border-radius: inherit;
  transition: width var(--duration-normal) var(--ease-out);
}

.progress-bar--under { background: var(--color-sage); }
.progress-bar--close { background: var(--color-terracotta); }
.progress-bar--over { background: var(--color-warning); }
.progress-bar--protein { background: var(--color-protein); }
.progress-bar--carbs { background: var(--color-carbs); }
.progress-bar--fat { background: var(--color-fat); }

/* Macros Section */
.progress-macros {
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: var(--space-4);
  width: 100%;
}

.macro {
  text-align: center;
}

.macro-ring {
  width: 56px;
  height: 56px;
  margin: 0 auto var(--space-2);
  position: relative;
}

.macro-ring svg {
  transform: rotate(-90deg);
  width: 100%;
  height: 100%;
}

.macro-ring-bg {
  fill: none;
  stroke: var(--color-sage-pale);
  stroke-width: 5;
}

.macro-ring-fill {
  fill: none;
  stroke-width: 5;
  stroke-linecap: round;
  transition: stroke-dashoffset var(--duration-normal) var(--ease-out);
}

.macro-ring-fill--protein { stroke: var(--color-protein); }
.macro-ring-fill--carbs { stroke: var(--color-carbs); }
.macro-ring-fill--fat { stroke: var(--color-fat); }

.macro-ring-value {
  position: absolute;
  inset: 0;
  display: flex;
  align-items: center;
  justify-content: center;
  font-family: var(--font-display);
  font-size: 0.875rem;
  font-weight: 600;
  color: var(--color-espresso);
}

.macro-label {
  font-size: 0.6875rem;
  font-weight: 600;
  color: var(--color-warm-gray);
  text-transform: uppercase;
  letter-spacing: 0.08em;
}

.macro-detail {
  font-size: 0.75rem;
  color: var(--color-muted);
  margin-top: 2px;
}

/* Legacy macro bar layout */
.macro-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: var(--space-1);
}

.macro-value {
  font-size: 0.875rem;
  font-weight: 500;
  color: var(--color-warm-gray);
}

/* ============================================
   ENTRY LIST
   ============================================ */

.entry-list {
  margin-bottom: var(--space-5);
}

.entry-list-title {
  font-family: var(--font-display);
  font-size: 0.875rem;
  font-weight: 600;
  color: var(--color-warm-gray);
  text-transform: uppercase;
  letter-spacing: 0.1em;
  margin-bottom: var(--space-4);
}

.entry-list-empty {
  text-align: center;
  padding: var(--space-10) var(--space-5);
  color: var(--color-muted);
}

.entry-list-empty-hint {
  font-size: 0.875rem;
  margin-top: var(--space-2);
  color: var(--color-sage-light);
}

.entry-card {
  background: var(--color-white);
  border-radius: var(--radius-lg);
  padding: var(--space-4) var(--space-5);
  margin-bottom: var(--space-3);
  box-shadow: var(--shadow-sm);
  display: grid;
  grid-template-columns: 1fr auto;
  grid-template-rows: auto auto;
  gap: var(--space-1) var(--space-3);
  align-items: center;
  transition: transform var(--duration-fast), box-shadow var(--duration-fast);
}

.entry-card:hover {
  transform: translateY(-2px);
  box-shadow: var(--shadow-md);
}

.entry-main {
  display: flex;
  justify-content: space-between;
  align-items: center;
  cursor: pointer;
  grid-column: 1;
}

.entry-name {
  font-weight: 600;
  color: var(--color-espresso);
}

.entry-calories {
  font-family: var(--font-display);
  font-weight: 700;
  color: var(--color-sage);
}

.entry-macros {
  grid-column: 1;
  display: flex;
  gap: var(--space-3);
}

.entry-macro {
  font-size: 0.75rem;
  color: var(--color-muted);
  display: flex;
  align-items: center;
  gap: 4px;
}

.entry-macro::before {
  content: '';
  width: 6px;
  height: 6px;
  border-radius: 50%;
}

.entry-macro--protein::before { background: var(--color-protein); }
.entry-macro--carbs::before { background: var(--color-carbs); }
.entry-macro--fat::before { background: var(--color-fat); }

.entry-delete {
  grid-column: 2;
  grid-row: 1 / 3;
  background: none;
  border: none;
  padding: var(--space-2);
  cursor: pointer;
  color: var(--color-muted);
  transition: all var(--duration-fast);
  border-radius: var(--radius-sm);
}

.entry-delete:hover {
  color: var(--color-terracotta);
  background: var(--color-terracotta-pale);
}

.entry-delete:focus-visible {
  outline: 2px solid var(--color-sage);
  outline-offset: 2px;
}

/* ============================================
   FORM OVERLAY & ENTRY FORM
   ============================================ */

.form-overlay {
  position: fixed;
  inset: 0;
  background: rgba(61,61,61,0.3);
  backdrop-filter: blur(4px);
  display: flex;
  align-items: flex-end;
  justify-content: center;
  z-index: 100;
  animation: fadeIn var(--duration-fast) var(--ease-out);
}

@keyframes fadeIn {
  from { opacity: 0; }
  to { opacity: 1; }
}

.entry-form {
  background: var(--color-white);
  border-radius: var(--radius-xl) var(--radius-xl) 0 0;
  padding: var(--space-6);
  width: 100%;
  max-width: 480px;
  animation: slideUp var(--duration-normal) var(--ease-out);
}

@keyframes slideUp {
  from { transform: translateY(100%); }
  to { transform: translateY(0); }
}

.form-title {
  font-family: var(--font-display);
  font-size: 1.5rem;
  font-weight: 600;
  color: var(--color-sage);
  margin-bottom: var(--space-5);
}

.form-field {
  margin-bottom: var(--space-4);
}

.form-label {
  display: block;
  font-size: 0.8125rem;
  font-weight: 600;
  color: var(--color-warm-gray);
  margin-bottom: var(--space-2);
  text-transform: uppercase;
  letter-spacing: 0.05em;
}

.form-input {
  width: 100%;
  padding: var(--space-3) var(--space-4);
  font-family: var(--font-body);
  font-size: 1rem;
  border: 2px solid var(--color-sage-pale);
  border-radius: var(--radius-md);
  background: var(--color-cream);
  color: var(--color-espresso);
  transition: all var(--duration-fast);
}

.form-input:focus {
  outline: none;
  border-color: var(--color-sage);
  background: var(--color-white);
  box-shadow: 0 0 0 4px rgba(92,107,84,0.1);
}

.form-input::placeholder {
  color: var(--color-muted);
}

.form-row {
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: var(--space-3);
}

.form-actions {
  display: flex;
  gap: var(--space-3);
  margin-top: var(--space-6);
}

/* ============================================
   BUTTONS
   ============================================ */

.btn {
  flex: 1;
  padding: var(--space-4) var(--space-5);
  font-family: var(--font-body);
  font-size: 1rem;
  font-weight: 600;
  border-radius: var(--radius-md);
  border: none;
  cursor: pointer;
  transition: all var(--duration-fast);
}

.btn--primary {
  background: var(--color-sage);
  color: var(--color-white);
}

.btn--primary:hover {
  background: var(--color-sage-light);
  transform: translateY(-1px);
}

.btn--primary:active {
  transform: translateY(0);
}

.btn--secondary {
  background: var(--color-sage-pale);
  color: var(--color-sage);
}

.btn--secondary:hover {
  background: var(--color-sage-faint);
}

.btn--danger {
  background: var(--color-terracotta);
  color: var(--color-white);
}

.btn--danger:hover {
  background: var(--color-terracotta-light);
}

.btn--sm {
  padding: var(--space-2) var(--space-3);
  font-size: 0.875rem;
}

.btn:focus-visible {
  outline: 2px solid var(--color-sage);
  outline-offset: 2px;
}

/* ============================================
   MODAL
   ============================================ */

.modal-overlay {
  position: fixed;
  inset: 0;
  background: rgba(61,61,61,0.3);
  backdrop-filter: blur(4px);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 200;
  padding: var(--space-5);
  animation: fadeIn var(--duration-fast) var(--ease-out);
}

.modal {
  background: var(--color-white);
  border-radius: var(--radius-xl);
  padding: var(--space-6);
  width: 100%;
  max-width: 360px;
  animation: scaleIn var(--duration-normal) var(--ease-out);
  box-shadow: var(--shadow-lg);
}

@keyframes scaleIn {
  from {
    opacity: 0;
    transform: scale(0.9);
  }
  to {
    opacity: 1;
    transform: scale(1);
  }
}

.modal-title {
  font-family: var(--font-display);
  font-size: 1.25rem;
  font-weight: 600;
  color: var(--color-espresso);
  margin-bottom: var(--space-2);
}

.modal-message {
  font-size: 0.9375rem;
  color: var(--color-warm-gray);
  margin-bottom: var(--space-6);
  line-height: 1.5;
}

.modal-actions {
  display: flex;
  gap: var(--space-3);
}

/* ============================================
   FAB (Floating Action Button)
   ============================================ */

.fab {
  position: fixed;
  bottom: var(--space-6);
  right: var(--space-6);
  width: 64px;
  height: 64px;
  border-radius: var(--radius-full);
  background: var(--color-terracotta);
  color: var(--color-white);
  border: none;
  box-shadow: var(--shadow-lg);
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  transition: all var(--duration-fast);
}

.fab:hover {
  background: var(--color-terracotta-light);
  transform: scale(1.08);
  box-shadow: var(--shadow-glow);
}

.fab:active {
  transform: scale(0.95);
}

.fab:focus-visible {
  outline: 2px solid var(--color-terracotta);
  outline-offset: 4px;
}

@media (min-width: 480px) {
  .fab {
    right: calc(50% - 240px + var(--space-6));
  }
}

/* ============================================
   CALENDAR / DATE PICKER
   ============================================ */

.calendar-modal {
  background: var(--color-white);
  border-radius: var(--radius-xl);
  padding: var(--space-5);
  width: 100%;
  max-width: 340px;
  animation: scaleIn var(--duration-normal) var(--ease-out);
  box-shadow: var(--shadow-lg);
}

.calendar-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: var(--space-4);
}

.calendar-title {
  font-family: var(--font-display);
  font-size: 1.125rem;
  font-weight: 600;
  color: var(--color-sage);
}

.calendar-weekdays {
  display: grid;
  grid-template-columns: repeat(7, 1fr);
  gap: var(--space-1);
  margin-bottom: var(--space-2);
}

.calendar-weekday {
  text-align: center;
  font-size: 0.6875rem;
  font-weight: 600;
  color: var(--color-muted);
  text-transform: uppercase;
  letter-spacing: 0.05em;
  padding: var(--space-1);
}

.calendar-days {
  display: grid;
  grid-template-columns: repeat(7, 1fr);
  gap: var(--space-1);
}

.calendar-day {
  aspect-ratio: 1;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 0.875rem;
  font-weight: 500;
  border: none;
  background: none;
  border-radius: var(--radius-full);
  cursor: pointer;
  color: var(--color-espresso);
  transition: all var(--duration-fast);
}

.calendar-day:hover:not(:disabled):not(.calendar-day--empty) {
  background: var(--color-sage-pale);
}

.calendar-day--empty {
  cursor: default;
}

.calendar-day--today {
  font-weight: 700;
  color: var(--color-sage);
  border: 2px solid var(--color-sage-light);
}

.calendar-day--selected {
  background: var(--color-sage);
  color: var(--color-white);
}

.calendar-day--selected:hover:not(:disabled) {
  background: var(--color-sage-light);
}

.calendar-day--disabled {
  color: var(--color-muted);
  cursor: not-allowed;
}

.calendar-today-btn {
  width: 100%;
  margin-top: var(--space-4);
}

/* ============================================
   SETTINGS
   ============================================ */

.form-section-title {
  font-family: var(--font-display);
  font-size: 0.8125rem;
  font-weight: 600;
  color: var(--color-warm-gray);
  text-transform: uppercase;
  letter-spacing: 0.1em;
  margin-top: var(--space-5);
  margin-bottom: var(--space-3);
}

.form-section-title:first-of-type {
  margin-top: 0;
}

.data-actions {
  display: flex;
  gap: var(--space-3);
  margin-bottom: var(--space-4);
}

.data-actions .btn {
  flex: 1;
  font-size: 0.875rem;
  padding: var(--space-3) var(--space-4);
}

.status-message {
  text-align: center;
  font-size: 0.875rem;
  font-weight: 500;
  color: var(--color-sage);
  margin-bottom: var(--space-4);
  padding: var(--space-3);
  background: var(--color-sage-faint);
  border-radius: var(--radius-sm);
}

/* ============================================
   PROFILE STYLES
   ============================================ */

.profile-modal {
  background: var(--color-white);
  border-radius: var(--radius-xl);
  padding: var(--space-6);
  width: 100%;
  max-width: 360px;
  animation: scaleIn var(--duration-normal) var(--ease-out);
  box-shadow: var(--shadow-lg);
}

.profile-empty {
  text-align: center;
  color: var(--color-muted);
  padding: var(--space-5) 0;
}

.profile-list {
  display: flex;
  flex-direction: column;
  gap: var(--space-2);
  margin-bottom: var(--space-4);
}

.profile-item {
  width: 100%;
}

.profile-select-btn {
  width: 100%;
  padding: var(--space-4);
  font-family: var(--font-body);
  font-size: 1.125rem;
  font-weight: 600;
  background: var(--color-sage-faint);
  border: 2px solid var(--color-sage-pale);
  border-radius: var(--radius-md);
  cursor: pointer;
  transition: all var(--duration-fast);
  color: var(--color-espresso);
}

.profile-select-btn:hover {
  background: var(--color-sage-pale);
  border-color: var(--color-sage-light);
  transform: translateY(-2px);
}

.profile-pin-entry {
  display: flex;
  align-items: center;
  gap: var(--space-2);
  padding: var(--space-3);
  background: var(--color-sage-faint);
  border-radius: var(--radius-md);
}

.profile-name {
  font-weight: 600;
  flex: 1;
}

.pin-input {
  width: 64px;
  padding: var(--space-2);
  font-family: var(--font-body);
  font-size: 1rem;
  text-align: center;
  border: 2px solid var(--color-sage-pale);
  border-radius: var(--radius-sm);
  background: var(--color-white);
}

.pin-input:focus {
  outline: none;
  border-color: var(--color-sage);
}

.profile-add-btn {
  width: 100%;
  margin-top: var(--space-2);
}

.profile-add-form {
  display: flex;
  flex-direction: column;
  gap: var(--space-3);
  margin-top: var(--space-4);
  padding-top: var(--space-4);
  border-top: 1px solid var(--color-sage-pale);
}

.error-message {
  color: var(--color-terracotta);
  font-size: 0.875rem;
  font-weight: 500;
  text-align: center;
  margin-top: var(--space-2);
}

.profile-management {
  margin-bottom: var(--space-5);
}

.current-profile-name {
  font-family: var(--font-display);
  font-size: 1.375rem;
  font-weight: 600;
  color: var(--color-sage);
  margin-bottom: var(--space-3);
}

.profile-actions {
  display: flex;
  flex-wrap: wrap;
  gap: var(--space-2);
}

.profile-actions .btn {
  flex: 1;
  min-width: 120px;
}

.delete-confirm {
  margin-top: var(--space-4);
  padding: var(--space-4);
  background: var(--color-terracotta-pale);
  border-radius: var(--radius-md);
}

.delete-confirm p {
  margin-bottom: var(--space-3);
  color: var(--color-espresso);
}

.profile-hint {
  color: var(--color-muted);
  font-size: 0.875rem;
  margin-bottom: var(--space-3);
}

