  transition: all 0.2s;
}

.btn--primary {
  background: var(--primary-600);
  color: var(--white);
}

.btn--primary:hover {
  background: var(--primary-700);
}

.btn--secondary {
  background: var(--gray-100);
  color: var(--gray-600);
}

.btn--secondary:hover {
  background: var(--gray-200);
}

.btn:focus-visible {
  outline: 2px solid var(--primary-500);
  outline-offset: 2px;
}

.btn--danger {
  background: var(--red-500);
  color: var(--white);
}

.btn--danger:hover {
