content.content:
     1:root {
     2  /* Primary - Teal */
     3  --primary-50: #ecfeff;
     4  --primary-100: #cffafe;
     5  --primary-200: #a5f3fc;
     6  --primary-400: #22d3ee;
     7  --primary-500: #06b6d4;
     8  --primary-600: #0891b2;
     9  --primary-700: #0e7490;
    10
    11  /* Accent - Coral */
    12  --accent-400: #fb923c;
    13  --accent-500: #f97316;
    14  --accent-600: #ea580c;
    15
    16  /* Neutrals - Warm tinted */
    17  --white: #ffffff;
    18  --gray-50: #fafaf9;
    19  --gray-100: #f5f5f4;
    20  --gray-200: #e7e5e4;
    21  --gray-300: #d6d3d1;
    22  --gray-400: #a8a29e;
    23  --gray-500: #78716c;
    24  --gray-600: #57534e;
    25  --gray-700: #44403c;
    26
    27  /* Danger */
    28  --red-500: #ef4444;
    29  --red-600: #dc2626;
    30
    31  /* Macros */
    32  --color-protein: #8b5cf6;
    33  --color-carbs: #f59e0b;
    34  --color-fat: #ec4899;
    35
    36  /* Typography & Layout */
    37  --font-family: 'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
    38  --border-radius: 12px;
    39  --border-radius-sm: 8px;
    40
    41  /* Shadows - Teal tinted */
    42  --shadow-sm: 0 1px 2px rgba(6, 182, 212, 0.08);
    43  --shadow: 0 4px 6px -1px rgba(6, 182, 212, 0.1);
    44  --shadow-lg: 0 10px 15px -3px rgba(6, 182, 212, 0.15);
    45  --shadow-coral: 0 8px 24px rgba(249, 115, 22, 0.3);
    46}
    47
    48* {
    49  box-sizing: border-box;
    50  margin: 0;
    51  padding: 0;
    52}
    53
    54body {
    55  font-family: var(--font-family);
    56  background: var(--primary-50);
    57  color: var(--gray-700);
    58  line-height: 1.6;
    59  min-height: 100vh;
    60}
    61
    62#app {
    63  max-width: 480px;
    64  margin: 0 auto;
    65  padding: 16px;
    66  padding-bottom: 100px;
    67}
    68
    69/* Header */
    70.header {
    71  margin-bottom: 24px;
    72}
    73
    74.header-top {
    75  display: flex;
    76  justify-content: space-between;
    77  align-items: center;
    78  margin-bottom: 16px;
    79}
    80
    81.header-title {
    82  font-size: 1.5rem;
    83  font-weight: 600;
    84  color: var(--primary-700);
    85}
    86
    87.date-nav {
    88  display: flex;
    89  align-items: center;
    90  justify-content: center;
    91  gap: 8px;
    92}
    93
    94.date-display {
    95  background: var(--white);
    96  border: 1px solid var(--primary-200);
    97  border-radius: var(--border-radius-sm);
    98  padding: 8px 20px;
    99  font-size: 1rem;
   100  font-weight: 500;
   101  color: var(--primary-700);
   102  cursor: pointer;
   103  transition: all 0.2s;
   104}
   105
   106.date-display:hover {
   107  background: var(--primary-100);
   108  border-color: var(--primary-400);
   109}
   110
   111.btn-icon {
   112  background: var(--white);
   113  border: 1px solid var(--primary-200);
   114  border-radius: var(--border-radius-sm);
   115  width: 40px;
   116  height: 40px;
   117  display: flex;
   118  align-items: center;
   119  justify-content: center;
   120  cursor: pointer;
   121  color: var(--primary-600);
   122  transition: all 0.2s;
   123}
   124
   125.btn-icon:hover:not(:disabled) {
   126  background: var(--primary-100);
   127  border-color: var(--primary-400);
   128}
   129
   130.btn-icon:disabled {
   131  opacity: 0.4;
   132  cursor: not-allowed;
   133}
   134
   135.btn-icon:focus-visible,
   136.date-display:focus-visible {
   137  outline: 2px solid var(--primary-500);
   138  outline-offset: 2px;
   139}
   140
   141/* Progress Card */
   142.progress-card {
   143  background: var(--white);
   144  border-radius: var(--border-radius);
   145  padding: 20px;
   146  box-shadow: var(--shadow-sm);
   147  margin-bottom: 20px;
   148}
   149
   150.progress-main {
   151  margin-bottom: 20px;
   152}
   153
   154.progress-calories {
   155  display: flex;
   156  align-items: baseline;
   157  gap: 4px;
   158  margin-bottom: 8px;
   159}
   160
   161.progress-value {
   162  font-size: 2rem;
   163  font-weight: 600;
   164  color: var(--accent-500);
   165}
   166
   167.progress-label {
   168  font-size: 0.875rem;
   169  color: var(--gray-500);
   170}
   171
   172.progress-bar-container {
   173  height: 12px;
   174  background: var(--primary-200);
   175  border-radius: 6px;
   176  overflow: hidden;
   177}
   178
   179.progress-bar-container--sm {
   180  height: 6px;
   181  border-radius: 3px;
   182}
   183
   184.progress-bar {
   185  height: 100%;
   186  border-radius: inherit;
   187  transition: width 0.3s ease;
   188}
   189
   190.progress-bar--under {
   191  background: var(--primary-500);
   192}
   193
   194.progress-bar--close {
   195  background: var(--accent-500);
   196}
   197
   198.progress-bar--over {
   199  background: var(--red-500);
   200}
   201
   202.progress-bar--protein {
   203  background: var(--color-protein);
   204}
   205
   206.progress-bar--carbs {
   207  background: var(--color-carbs);
   208}
   209
   210.progress-bar--fat {
   211  background: var(--color-fat);
   212}
   213
   214.progress-macros {
   215  display: flex;
   216  flex-direction: column;
   217  gap: 12px;
   218}
   219
   220.macro {
   221  display: flex;
   222  flex-direction: column;
   223  gap: 4px;
   224}
   225
   226.macro-header {
   227  display: flex;
   228  justify-content: space-between;
   229  align-items: center;
   230}
   231
   232.macro-label {
   233  font-size: 0.75rem;
   234  font-weight: 500;
   235  color: var(--gray-600);
   236  text-transform: uppercase;
   237  letter-spacing: 0.05em;
   238}
   239
   240.macro-value {
   241  font-size: 0.875rem;
   242  color: var(--gray-500);
   243}
   244
   245/* Entry List */
   246.entry-list {
   247  margin-bottom: 20px;
   248}
   249
   250.entry-list-title {
   251  font-size: 0.875rem;
   252  font-weight: 600;
   253  color: var(--gray-500);
   254  text-transform: uppercase;
   255  letter-spacing: 0.05em;
   256  margin-bottom: 12px;
   257}
   258
   259.entry-list-empty {
   260  text-align: center;
   261  padding: 40px 20px;
   262  color: var(--gray-400);
   263}
   264
   265.entry-list-empty-hint {
   266  font-size: 0.875rem;
   267  margin-top: 4px;
   268}
   269
   270.entry-card {
   271  background: var(--white);
   272  border-radius: var(--border-radius-sm);
   273  padding: 14px 16px;
   274  margin-bottom: 8px;
   275  box-shadow: var(--shadow-sm);
   276  display: grid;
   277  grid-template-columns: 1fr auto;
   278  grid-template-rows: auto auto;
   279  gap: 4px 12px;
   280  align-items: center;
   281}
   282
   283.entry-main {
   284  display: flex;
   285  justify-content: space-between;
   286  align-items: center;
   287  cursor: pointer;
   288  grid-column: 1;
   289  transition: opacity 0.2s;
   290}
   291
   292.entry-main:hover {
   293  opacity: 0.7;
   294}
   295
   296.entry-name {
   297  font-weight: 500;
   298  color: var(--gray-700);
   299}
   300
   301.entry-calories {
   302  font-weight: 600;
   303  color: var(--primary-600);
   304}
   305
   306.entry-macros {
   307  grid-column: 1;
   308  display: flex;
   309  gap: 12px;
   310}
   311
   312.entry-macro {
   313  font-size: 0.75rem;
   314  color: var(--gray-400);
   315}
   316
   317.entry-delete {
   318  grid-column: 2;
   319  grid-row: 1 / 3;
   320  background: none;
   321  border: none;
   322  padding: 8px;
   323  cursor: pointer;
   324  color: var(--gray-400);
   325  transition: color 0.2s;
   326  border-radius: var(--border-radius-sm);
   327}
   328
   329.entry-delete:hover {
   330  color: var(--red-500);
   331  background: var(--gray-50);
   332}
   333
   334.entry-delete:focus-visible {
   335  outline: 2px solid var(--primary-500);
   336  outline-offset: 2px;
   337}
   338
   339/* Form Overlay */
   340.form-overlay {
   341  position: fixed;
   342  inset: 0;
   343  background: rgba(14, 116, 144, 0.4);
   344  display: flex;
   345  align-items: flex-end;
   346  justify-content: center;
   347  z-index: 100;
   348  animation: fadeIn 0.2s ease;
   349}
   350
   351@keyframes fadeIn {
   352  from { opacity: 0; }
   353  to { opacity: 1; }
   354}
   355
   356.entry-form {
   357  background: var(--white);
   358  border-radius: var(--border-radius) var(--border-radius) 0 0;
   359  padding: 24px;
   360  width: 100%;
   361  max-width: 480px;
   362  animation: slideUp 0.3s ease;
   363}
   364
   365@keyframes slideUp {
   366  from { transform: translateY(100%); }
   367  to { transform: translateY(0); }
   368}
   369
   370.form-title {
   371  font-size: 1.25rem;
   372  font-weight: 600;
   373  color: var(--primary-700);
   374  margin-bottom: 20px;
   375}
   376
   377.form-field {
   378  margin-bottom: 16px;
   379}
   380
   381.form-label {
   382  display: block;
   383  font-size: 0.875rem;
   384  font-weight: 500;
   385  color: var(--gray-600);
   386  margin-bottom: 6px;
   387}
   388
   389.form-input {
   390  width: 100%;
   391  padding: 12px 14px;
   392  font-size: 1rem;
   393  border: 1px solid var(--gray-200);
   394  border-radius: var(--border-radius-sm);
   395  background: var(--gray-50);
   396  color: var(--gray-700);
   397  transition: all 0.2s;
   398}
   399
   400.form-input:focus {
   401  outline: none;
   402  border-color: var(--primary-400);
   403  background: var(--white);
   404  box-shadow: 0 0 0 3px rgba(6, 182, 212, 0.15);
   405}
   406
   407.form-input::placeholder {
   408  color: var(--gray-400);
   409}
   410
   411.form-row {
   412  display: grid;
   413  grid-template-columns: repeat(3, 1fr);
   414  gap: 12px;
   415}
   416
   417.form-actions {
   418  display: flex;
   419  gap: 12px;
   420  margin-top: 24px;
   421}
   422
   423/* Buttons */
   424.btn {
   425  flex: 1;
   426  padding: 14px 20px;
   427  font-size: 1rem;
   428  font-weight: 500;
   429  border-radius: var(--border-radius-sm);
   430  border: none;
   431  cursor: pointer;
   432  transition: all 0.2s;
   433}
   434
   435.btn--primary {
   436  background: var(--primary-600);
   437  color: var(--white);
   438}
   439
   440.btn--primary:hover {
   441  background: var(--primary-700);
   442}
   443
   444.btn--secondary {
   445  background: var(--gray-100);
   446  color: var(--gray-600);
   447}
   448
   449.btn--secondary:hover {
   450  background: var(--gray-200);
   451}
   452
   453.btn:focus-visible {
   454  outline: 2px solid var(--primary-500);
   455  outline-offset: 2px;
   456}
   457
   458.btn--danger {
   459  background: var(--red-500);
   460  color: var(--white);
   461}
   462
   463.btn--danger:hover {
   464  background: var(--red-600);
   465}
   466
   467/* Modal */
   468.modal-overlay {
   469  position: fixed;
   470  inset: 0;
   471  background: rgba(14, 116, 144, 0.4);
   472  display: flex;
   473  align-items: center;
   474  justify-content: center;
   475  z-index: 200;
   476  padding: 20px;
   477  animation: fadeIn 0.2s ease;
   478}
   479
   480.modal {
   481  background: var(--white);
   482  border-radius: var(--border-radius);
   483  padding: 24px;
   484  width: 100%;
   485  max-width: 340px;
   486  animation: scaleIn 0.2s ease;
   487}
   488
   489@keyframes scaleIn {
   490  from {
   491    opacity: 0;
   492    transform: scale(0.95);
   493  }
   494  to {
   495    opacity: 1;
   496    transform: scale(1);
   497  }
   498}
   499
   500.modal-title {
   501  font-size: 1.125rem;
   502  font-weight: 600;
   503  color: var(--gray-700);
   504  margin-bottom: 8px;
   505}
   506
   507.modal-message {
   508  font-size: 0.9375rem;
   509  color: var(--gray-500);
   510  margin-bottom: 24px;
   511}
   512
   513.modal-actions {
   514  display: flex;
   515  gap: 12px;
   516}
   517
   518/* FAB */
   519.fab {
   520  position: fixed;
   521  bottom: 24px;
   522  right: 24px;
   523  width: 60px;
   524  height: 60px;
   525  border-radius: 50%;
   526  background: var(--primary-600);
   527  color: var(--white);
   528  border: none;
   529  box-shadow: var(--shadow-coral);
   530  cursor: pointer;
   531  display: flex;
   532  align-items: center;
   533  justify-content: center;
   534  transition: all 0.2s;
   535}
   536
   537.fab:hover {
   538  background: var(--primary-700);
   539  transform: scale(1.05);
   540}
   541
   542.fab:active {
   543  transform: scale(0.95);
   544}
   545
   546.fab:focus-visible {
   547  outline: 2px solid var(--primary-700);
   548  outline-offset: 2px;
   549}
   550
   551@media (min-width: 480px) {
   552  .fab {
   553    right: calc(50% - 240px + 24px);
   554  }
   555}
   556
   557/* Calendar */
   558.calendar-modal {
   559  background: var(--white);
   560  border-radius: var(--border-radius);
   561  padding: 20px;
   562  width: 100%;
   563  max-width: 320px;
   564  animation: scaleIn 0.2s ease;
   565}
   566
   567.calendar-header {
   568  display: flex;
   569  justify-content: space-between;
   570  align-items: center;
   571  margin-bottom: 16px;
   572}
   573
   574.calendar-title {
   575  font-size: 1rem;
   576  font-weight: 600;
   577  color: var(--primary-700);
   578}
   579
   580.calendar-weekdays {
   581  display: grid;
   582  grid-template-columns: repeat(7, 1fr);
   583  gap: 4px;
   584  margin-bottom: 8px;
   585}
   586
   587.calendar-weekday {
   588  text-align: center;
   589  font-size: 0.75rem;
   590  font-weight: 500;
   591  color: var(--gray-500);
   592  padding: 4px;
   593}
   594
   595.calendar-days {
   596  display: grid;
   597  grid-template-columns: repeat(7, 1fr);
   598  gap: 4px;
   599}
   600
   601.calendar-day {
   602  aspect-ratio: 1;
   603  display: flex;
   604  align-items: center;
   605  justify-content: center;
   606  font-size: 0.875rem;
   607  border: none;
   608  background: none;
   609  border-radius: 50%;
   610  cursor: pointer;
   611  color: var(--gray-700);
   612  transition: all 0.2s;
   613}
   614
   615.calendar-day:hover:not(:disabled):not(.calendar-day--empty) {
   616  background: var(--primary-100);
   617}
   618
   619.calendar-day--empty {
   620  cursor: default;
   621}
   622
   623.calendar-day--today {
   624  font-weight: 600;
   625  color: var(--primary-600);
   626  border: 2px solid var(--primary-400);
   627}
   628
   629.calendar-day--selected {
   630  background: var(--primary-600);
   631  color: var(--white);
   632}
   633
   634.calendar-day--selected:hover:not(:disabled) {
   635  background: var(--primary-700);
   636}
   637
   638.calendar-day--disabled {
   639  color: var(--gray-300);
   640  cursor: not-allowed;
   641}
   642
   643.calendar-today-btn {
   644  width: 100%;
   645  margin-top: 16px;
   646}
   647
   648/* Settings Sections */
   649.form-section-title {
   650  font-size: 0.875rem;
   651  font-weight: 600;
   652  color: var(--gray-500);
   653  text-transform: uppercase;
   654  letter-spacing: 0.05em;
   655  margin-top: 20px;
   656  margin-bottom: 12px;
   657}
   658
   659.form-section-title:first-of-type {
   660  margin-top: 0;
   661}
   662
   663.data-actions {
   664  display: flex;
   665  gap: 12px;
   666  margin-bottom: 16px;
   667}
   668
   669.data-actions .btn {
   670  flex: 1;
   671  font-size: 0.875rem;
   672  padding: 10px 16px;
   673}
   674
   675.status-message {
   676  text-align: center;
   677  font-size: 0.875rem;
   678  color: var(--primary-600);
   679  margin-bottom: 16px;
   680}
   681
   682/* Profile Styles */
   683.profile-modal {
   684  background: var(--white);
   685  border-radius: var(--border-radius);
   686  padding: 24px;
   687  width: 100%;
   688  max-width: 340px;
   689  animation: scaleIn 0.2s ease;
   690}
   691
   692.profile-empty {
   693  text-align: center;
   694  color: var(--gray-500);
   695  padding: 20px 0;
   696}
   697
   698.profile-list {
   699  display: flex;
   700  flex-direction: column;
   701  gap: 8px;
   702  margin-bottom: 16px;
   703}
   704
   705.profile-item {
   706  width: 100%;
   707}
   708
   709.profile-select-btn {
   710  width: 100%;
   711  padding: 16px;
   712  font-size: 1.125rem;
   713  font-weight: 500;
   714  background: var(--gray-50);
   715  border: 1px solid var(--gray-200);
   716  border-radius: var(--border-radius-sm);
   717  cursor: pointer;
   718  transition: all 0.2s;
   719  color: var(--gray-700);
   720}
   721
   722.profile-select-btn:hover {
   723  background: var(--primary-100);
   724  border-color: var(--primary-400);
   725}
   726
   727.profile-pin-entry {
   728  display: flex;
   729  align-items: center;
   730  gap: 8px;
   731  padding: 8px;
   732  background: var(--gray-50);
   733  border-radius: var(--border-radius-sm);
   734}
   735
   736.profile-name {
   737  font-weight: 500;
   738  flex: 1;
   739}
   740
   741.pin-input {
   742  width: 60px;
   743  padding: 8px;
   744  font-size: 1rem;
   745  text-align: center;
   746  border: 1px solid var(--gray-300);
   747  border-radius: var(--border-radius-sm);
   748}
   749
   750.pin-input:focus {
   751  outline: none;
   752  border-color: var(--primary-500);
   753}
   754
   755.btn--sm {
   756  padding: 8px 12px;
   757  font-size: 0.875rem;
   758}
   759
   760.profile-add-btn {
   761  width: 100%;
   762  margin-top: 8px;
   763}
   764
   765.profile-add-form {
   766  display: flex;
   767  flex-direction: column;
   768  gap: 12px;
   769  margin-top: 16px;
   770  padding-top: 16px;
   771  border-top: 1px solid var(--gray-200);
   772}
   773
   774.error-message {
   775  color: var(--red-500);
   776  font-size: 0.875rem;
   777  text-align: center;
   778  margin-top: 8px;
   779}
   780
   781.profile-management {
   782  margin-bottom: 20px;
   783}
   784
   785.current-profile-name {
   786  font-size: 1.25rem;
   787  font-weight: 600;
   788  color: var(--primary-700);
   789  margin-bottom: 12px;
   790}
   791
   792.profile-actions {
   793  display: flex;
   794  flex-wrap: wrap;
   795  gap: 8px;
   796}
   797
   798.profile-actions .btn {
   799  flex: 1;
   800  min-width: 120px;
   801}
   802
   803.delete-confirm {
   804  margin-top: 16px;
   805  padding: 16px;
   806  background: var(--gray-50);
   807  border-radius: var(--border-radius-sm);
   808}
   809
   810.delete-confirm p {
   811  margin-bottom: 12px;
   812  color: var(--gray-600);
   813}
   814
   815.header-profile {
   816  display: flex;
   817  align-items: center;
   818  gap: 8px;
   819}
   820
   821.header-profile-name {
   822  font-size: 0.875rem;
   823  color: var(--gray-500);
   824}
   825
   826.btn-lock {
   827  background: none;
