    30  --color-protein: #8B7355;
    31  --color-carbs: #C4A35A;
    32  --color-fat: #B08D7A;
    33
    34  /* Spacing */
    35  --space-1: 4px;
    36  --space-2: 8px;
    37  --space-3: 12px;
    38  --space-4: 16px;
    39  --space-5: 20px;
    40  --space-6: 24px;
    41  --space-8: 32px;
    42  --space-10: 40px;
    43
    44  /* Borders */
    45  --radius-sm: 8px;
    46  --radius-md: 12px;
    47  --radius-lg: 20px;
    48  --radius-xl: 28px;
    49  --radius-full: 9999px;
    50
    51  /* Shadows */
    52  --shadow-sm: 0 1px 3px rgba(92,107,84,0.06);
    53  --shadow-md: 0 4px 16px rgba(92,107,84,0.08);
    54  --shadow-lg: 0 8px 32px rgba(92,107,84,0.1);
    55  --shadow-glow: 0 0 40px rgba(193,123,95,0.2);
    56
    57  /* Motion */
    58  --duration-fast: 150ms;
    59  --duration-normal: 250ms;
    60  --duration-slow: 400ms;
    61  --ease-out: cubic-bezier(0.16, 1, 0.3, 1);
    62  --ease-in-out: cubic-bezier(0.65, 0, 0.35, 1);
    63}
    64
    65/* ============================================
    66   BASE RESET
    67   ============================================ */
    68
    69* {
    70  box-sizing: border-box;
    71  margin: 0;
    72  padding: 0;
    73}
    74
    75body {
    76  font-family: var(--font-body);
    77  background: var(--color-cream);
    78  color: var(--color-espresso);
    79  line-height: 1.6;
    80  min-height: 100vh;
    81  -webkit-font-smoothing: antialiased;
    82  -moz-osx-font-smoothing: grayscale;
    83}
    84
    85/* Subtle background atmosphere */
    86body::before {
    87  content: '';
    88  position: fixed;
    89  top: -20%;
    90  right: -10%;
    91  width: 60%;
    92  height: 60%;
    93  background: radial-gradient(ellipse at center, rgba(232,237,230,0.7) 0%, transparent 70%);
    94  pointer-events: none;
    95  z-index: -1;
    96}
    97
    98body::after {
    99  content: '';
   100  position: fixed;
   101  bottom: -10%;
   102  left: -10%;
   103  width: 50%;
   104  height: 50%;
   105  background: radial-gradient(ellipse at center, rgba(245,235,230,0.5) 0%, transparent 70%);
   106  pointer-events: none;
   107  z-index: -1;
   108}
   109
   110#app {
   111  max-width: 480px;
   112  margin: 0 auto;
   113  padding: var(--space-5);
   114  padding-bottom: 120px;
   115}
   116
   117/* ============================================
   118   HEADER
   119   ============================================ */
   120
   121.header {
   122  margin-bottom: var(--space-6);
   123}
   124
   125.header-top {
   126  display: flex;
   127  justify-content: space-between;
   128  align-items: center;
   129  margin-bottom: var(--space-5);
   130}
   131
   132.header-title {
   133  font-family: var(--font-display);
   134  font-size: 1.75rem;
   135  font-weight: 600;
   136  color: var(--color-sage);
   137  letter-spacing: -0.02em;
   138}
   139
   140.header-profile {
   141  display: flex;
   142  align-items: center;
   143  gap: var(--space-2);
   144}
   145
   146.header-profile-name {
   147  font-size: 0.875rem;
   148  font-weight: 500;
   149  color: var(--color-warm-gray);
   150}
   151
   152.btn-lock {
   153  background: none;
   154  border: none;
   155  padding: var(--space-1);
   156  cursor: pointer;
   157  color: var(--color-muted);
   158  transition: color var(--duration-fast);
   159  border-radius: var(--radius-sm);
   160}
   161
   162.btn-lock:hover {
   163  color: var(--color-sage);
   164}
   165
   166/* Date Navigation */
   167.date-nav {
   168  display: flex;
   169  align-items: center;
   170  justify-content: center;
   171  gap: var(--space-3);
   172}
   173
   174.date-display {
   175  background: var(--color-white);
   176  border: 1px solid var(--color-sage-pale);
   177  border-radius: var(--radius-full);
   178  padding: var(--space-2) var(--space-5);
   179  font-family: var(--font-body);
   180  font-size: 0.9375rem;
   181  font-weight: 600;
   182  color: var(--color-sage);
   183  cursor: pointer;
   184  transition: all var(--duration-fast);
   185  box-shadow: var(--shadow-sm);
   186}
   187
   188.date-display:hover {
   189  background: var(--color-sage-faint);
   190  border-color: var(--color-sage-light);
   191}
   192
   193.btn-icon {
   194  background: var(--color-white);
   195  border: 1px solid var(--color-sage-pale);
   196  border-radius: var(--radius-full);
   197  width: 44px;
   198  height: 44px;
   199  display: flex;
   200  align-items: center;
   201  justify-content: center;
   202  cursor: pointer;
   203  color: var(--color-sage);
   204  transition: all var(--duration-fast);
   205  box-shadow: var(--shadow-sm);
   206}
   207
   208.btn-icon:hover:not(:disabled) {
   209  background: var(--color-sage-faint);
   210  border-color: var(--color-sage-light);
   211  transform: scale(1.05);
   212}
   213
   214.btn-icon:active:not(:disabled) {
   215  transform: scale(0.95);
   216}
   217
   218.btn-icon:disabled {
   219  opacity: 0.4;
   220  cursor: not-allowed;
   221}
   222
   223.btn-icon:focus-visible,
   224.date-display:focus-visible {
   225  outline: 2px solid var(--color-sage);
   226  outline-offset: 2px;
   227}
   228
   229/* ============================================
   230   PROGRESS CARD  Hero Element
   231   ============================================ */
   232
   233.progress-card {
   234  background: var(--color-white);
   235  border-radius: var(--radius-xl);
   236  padding: var(--space-6);
   237  box-shadow: var(--shadow-md);
   238  margin-bottom: var(--space-6);
   239  position: relative;
   240  overflow: hidden;
   241}
   242
   243/* Subtle decorative gradient */
   244.progress-card::before {
   245  content: '';
   246  position: absolute;
   247  top: 0;
   248  right: 0;
   249  width: 150px;
   250  height: 150px;
   251  background: radial-gradient(circle at top right, rgba(232,237,230,0.5) 0%, transparent 70%);
   252  pointer-events: none;
   253}
   254
   255.progress-main {
   256  display: flex;
   257  flex-direction: column;
   258  align-items: center;
   259  margin-bottom: var(--space-6);
   260  position: relative;
   261}
   262
   263/* Radial Progress Ring */
   264.progress-ring {
   265  position: relative;
   266  width: 180px;
   267  height: 180px;
   268  margin-bottom: var(--space-4);
   269}
   270
   271.progress-ring svg {
   272  transform: rotate(-90deg);
   273  width: 100%;
   274  height: 100%;
   275}
   276
   277.progress-ring-bg {
   278  fill: none;
   279  stroke: var(--color-sage-pale);
   280  stroke-width: 12;
   281}
   282
   283.progress-ring-fill {
   284  fill: none;
   285  stroke-width: 12;
   286  stroke-linecap: round;
   287  transition: stroke-dashoffset var(--duration-slow) var(--ease-out),
   288              stroke var(--duration-normal);
   289}
   290
   291.progress-ring-fill--under {
   292  stroke: var(--color-sage);
   293}
   294
   295.progress-ring-fill--close {
   296  stroke: var(--color-terracotta);
   297}
   298
   299.progress-ring-fill--over {
   300  stroke: var(--color-warning);
   301}
   302
   303.progress-ring-content {
   304  position: absolute;
   305  inset: 0;
   306  display: flex;
   307  flex-direction: column;
   308  align-items: center;
   309  justify-content: center;
   310}
   311
   312.progress-value {
   313  font-family: var(--font-display);
   314  font-size: 2.75rem;
   315  font-weight: 700;
   316  color: var(--color-espresso);
   317  line-height: 1;
   318  letter-spacing: -0.02em;
   319}
   320
   321.progress-label {
   322  font-size: 0.875rem;
   323  font-weight: 500;
   324  color: var(--color-muted);
   325  margin-top: var(--space-1);
   326}
   327
   328/* Legacy progress bar (kept for fallback) */
   329.progress-calories {
   330  display: flex;
   331  align-items: baseline;
   332  gap: var(--space-1);
   333  margin-bottom: var(--space-3);
   334}
   335
   336.progress-bar-container {
   337  height: 10px;
   338  background: var(--color-sage-pale);
   339  border-radius: var(--radius-full);
   340  overflow: hidden;
   341  width: 100%;
   342}
   343
   344.progress-bar-container--sm {
   345  height: 6px;
   346}
   347
   348.progress-bar {
   349  height: 100%;
   350  border-radius: inherit;
   351  transition: width var(--duration-normal) var(--ease-out);
   352}
   353
   354.progress-bar--under { background: var(--color-sage); }
   355.progress-bar--close { background: var(--color-terracotta); }
   356.progress-bar--over { background: var(--color-warning); }
   357.progress-bar--protein { background: var(--color-protein); }
   358.progress-bar--carbs { background: var(--color-carbs); }
   359.progress-bar--fat { background: var(--color-fat); }
   360
   361/* Macros Section */
   362.progress-macros {
   363  display: grid;
   364  grid-template-columns: repeat(3, 1fr);
   365  gap: var(--space-4);
   366  width: 100%;
   367}
   368
   369.macro {
   370  text-align: center;
   371}
   372
   373.macro-ring {
   374  width: 56px;
   375  height: 56px;
   376  margin: 0 auto var(--space-2);
   377  position: relative;
   378}
   379
   380.macro-ring svg {
   381  transform: rotate(-90deg);
   382  width: 100%;
   383  height: 100%;
   384}
   385
   386.macro-ring-bg {
   387  fill: none;
   388  stroke: var(--color-sage-pale);
   389  stroke-width: 5;
   390}
   391
   392.macro-ring-fill {
   393  fill: none;
   394  stroke-width: 5;
   395  stroke-linecap: round;
   396  transition: stroke-dashoffset var(--duration-normal) var(--ease-out);
   397}
   398
   399.macro-ring-fill--protein { stroke: var(--color-protein); }
   400.macro-ring-fill--carbs { stroke: var(--color-carbs); }
   401.macro-ring-fill--fat { stroke: var(--color-fat); }
   402
   403.macro-ring-value {
   404  position: absolute;
   405  inset: 0;
   406  display: flex;
   407  align-items: center;
   408  justify-content: center;
   409  font-family: var(--font-display);
   410  font-size: 0.875rem;
   411  font-weight: 600;
   412  color: var(--color-espresso);
   413}
   414
   415.macro-label {
   416  font-size: 0.6875rem;
   417  font-weight: 600;
   418  color: var(--color-warm-gray);
   419  text-transform: uppercase;
   420  letter-spacing: 0.08em;
   421}
   422
   423.macro-detail {
   424  font-size: 0.75rem;
   425  color: var(--color-muted);
   426  margin-top: 2px;
   427}
   428
   429/* Legacy macro bar layout */
   430.macro-header {
   431  display: flex;
   432  justify-content: space-between;
   433  align-items: center;
   434  margin-bottom: var(--space-1);
   435}
   436
   437.macro-value {
   438  font-size: 0.875rem;
   439  font-weight: 500;
   440  color: var(--color-warm-gray);
   441}
   442
   443/* ============================================
   444   ENTRY LIST
   445   ============================================ */
   446
   447.entry-list {
   448  margin-bottom: var(--space-5);
   449}
   450
   451.entry-list-title {
   452  font-family: var(--font-display);
   453  font-size: 0.875rem;
   454  font-weight: 600;
   455  color: var(--color-warm-gray);
   456  text-transform: uppercase;
   457  letter-spacing: 0.1em;
   458  margin-bottom: var(--space-4);
   459}
   460
   461.entry-list-empty {
   462  text-align: center;
   463  padding: var(--space-10) var(--space-5);
   464  color: var(--color-muted);
   465}
   466
   467.entry-list-empty-hint {
   468  font-size: 0.875rem;
   469  margin-top: var(--space-2);
   470  color: var(--color-sage-light);
   471}
   472
   473.entry-card {
   474  background: var(--color-white);
   475  border-radius: var(--radius-lg);
   476  padding: var(--space-4) var(--space-5);
   477  margin-bottom: var(--space-3);
   478  box-shadow: var(--shadow-sm);
   479  display: grid;
   480  grid-template-columns: 1fr auto;
   481  grid-template-rows: auto auto;
   482  gap: var(--space-1) var(--space-3);
   483  align-items: center;
   484  transition: transform var(--duration-fast), box-shadow var(--duration-fast);
   485}
   486
   487.entry-card:hover {
   488  transform: translateY(-2px);
   489  box-shadow: var(--shadow-md);
   490}
   491
   492.entry-main {
   493  display: flex;
   494  justify-content: space-between;
   495  align-items: center;
   496  cursor: pointer;
   497  grid-column: 1;
   498}
   499
   500.entry-name {
   501  font-weight: 600;
   502  color: var(--color-espresso);
   503}
   504
   505.entry-calories {
   506  font-family: var(--font-display);
   507  font-weight: 700;
   508  color: var(--color-sage);
   509}
   510
   511.entry-macros {
   512  grid-column: 1;
   513  display: flex;
   514  gap: var(--space-3);
   515}
   516
   517.entry-macro {
   518  font-size: 0.75rem;
   519  color: var(--color-muted);
   520  display: flex;
   521  align-items: center;
   522  gap: 4px;
   523}
   524
   525.entry-macro::before {
   526  content: '';
   527  width: 6px;
   528  height: 6px;
   529  border-radius: 50%;
   530}
   531
   532.entry-macro--protein::before { background: var(--color-protein); }
   533.entry-macro--carbs::before { background: var(--color-carbs); }
   534.entry-macro--fat::before { background: var(--color-fat); }
   535
   536.entry-delete {
   537  grid-column: 2;
   538  grid-row: 1 / 3;
   539  background: none;
   540  border: none;
   541  padding: var(--space-2);
   542  cursor: pointer;
   543  color: var(--color-muted);
   544  transition: all var(--duration-fast);
   545  border-radius: var(--radius-sm);
   546}
   547
   548.entry-delete:hover {
   549  color: var(--color-terracotta);
   550  background: var(--color-terracotta-pale);
   551}
   552
   553.entry-delete:focus-visible {
   554  outline: 2px solid var(--color-sage);
   555  outline-offset: 2px;
   556}
   557
   558/* ============================================
   559   FORM OVERLAY & ENTRY FORM
   560   ============================================ */
   561
   562.form-overlay {
   563  position: fixed;
   564  inset: 0;
   565  background: rgba(61,61,61,0.3);
   566  backdrop-filter: blur(4px);
   567  display: flex;
   568  align-items: flex-end;
   569  justify-content: center;
   570  z-index: 100;
   571  animation: fadeIn var(--duration-fast) var(--ease-out);
   572}
   573
   574@keyframes fadeIn {
   575  from { opacity: 0; }
   576  to { opacity: 1; }
   577}
   578
   579.entry-form {
   580  background: var(--color-white);
   581  border-radius: var(--radius-xl) var(--radius-xl) 0 0;
   582  padding: var(--space-6);
   583  width: 100%;
   584  max-width: 480px;
   585  animation: slideUp var(--duration-normal) var(--ease-out);
   586}
   587
   588@keyframes slideUp {
   589  from { transform: translateY(100%); }
   590  to { transform: translateY(0); }
   591}
   592
   593.form-title {
   594  font-family: var(--font-display);
   595  font-size: 1.5rem;
   596  font-weight: 600;
   597  color: var(--color-sage);
   598  margin-bottom: var(--space-5);
   599}
   600
   601.form-field {
   602  margin-bottom: var(--space-4);
   603}
   604
   605.form-label {
   606  display: block;
   607  font-size: 0.8125rem;
   608  font-weight: 600;
   609  color: var(--color-warm-gray);
   610  margin-bottom: var(--space-2);
   611  text-transform: uppercase;
   612  letter-spacing: 0.05em;
   613}
   614
   615.form-input {
   616  width: 100%;
   617  padding: var(--space-3) var(--space-4);
   618  font-family: var(--font-body);
   619  font-size: 1rem;
   620  border: 2px solid var(--color-sage-pale);
   621  border-radius: var(--radius-md);
   622  background: var(--color-cream);
   623  color: var(--color-espresso);
   624  transition: all var(--duration-fast);
   625}
   626
   627.form-input:focus {
   628  outline: none;
   629  border-color: var(--color-sage);
   630  background: var(--color-white);
   631  box-shadow: 0 0 0 4px rgba(92,107,84,0.1);
   632}
   633
   634.form-input::placeholder {
   635  color: var(--color-muted);
   636}
   637
   638.form-row {
   639  display: grid;
   640  grid-template-columns: repeat(3, 1fr);
   641  gap: var(--space-3);
   642}
   643
   644.form-actions {
   645  display: flex;
   646  gap: var(--space-3);
   647  margin-top: var(--space-6);
   648}
   649
   650/* ============================================
   651   BUTTONS
   652   ============================================ */
   653
   654.btn {
   655  flex: 1;
   656  padding: var(--space-4) var(--space-5);
   657  font-family: var(--font-body);
   658  font-size: 1rem;
   659  font-weight: 600;
   660  border-radius: var(--radius-md);
   661  border: none;
   662  cursor: pointer;
   663  transition: all var(--duration-fast);
   664}
   665
   666.btn--primary {
   667  background: var(--color-sage);
   668  color: var(--color-white);
   669}
   670
   671.btn--primary:hover {
   672  background: var(--color-sage-light);
   673  transform: translateY(-1px);
   674}
   675
   676.btn--primary:active {
   677  transform: translateY(0);
   678}
   679
   680.btn--secondary {
   681  background: var(--color-sage-pale);
   682  color: var(--color-sage);
   683}
   684
   685.btn--secondary:hover {
   686  background: var(--color-sage-faint);
   687}
   688
   689.btn--danger {
   690  background: var(--color-terracotta);
   691  color: var(--color-white);
   692}
   693
   694.btn--danger:hover {
   695  background: var(--color-terracotta-light);
   696}
   697
   698.btn--sm {
   699  padding: var(--space-2) var(--space-3);
   700  font-size: 0.875rem;
   701}
   702
   703.btn:focus-visible {
   704  outline: 2px solid var(--color-sage);
   705  outline-offset: 2px;
   706}
   707
   708/* ============================================
   709   MODAL
   710   ============================================ */
   711
   712.modal-overlay {
   713  position: fixed;
   714  inset: 0;
   715  background: rgba(61,61,61,0.3);
   716  backdrop-filter: blur(4px);
   717  display: flex;
   718  align-items: center;
   719  justify-content: center;
   720  z-index: 200;
   721  padding: var(--space-5);
   722  animation: fadeIn var(--duration-fast) var(--ease-out);
   723}
   724
   725.modal {
   726  background: var(--color-white);
   727  border-radius: var(--radius-xl);
   728  padding: var(--space-6);
   729  width: 100%;
   730  max-width: 360px;
   731  animation: scaleIn var(--duration-normal) var(--ease-out);
   732  box-shadow: var(--shadow-lg);
   733}
   734
   735@keyframes scaleIn {
   736  from {
   737    opacity: 0;
   738    transform: scale(0.9);
   739  }
   740  to {
   741    opacity: 1;
   742    transform: scale(1);
   743  }
   744}
   745
   746.modal-title {
   747  font-family: var(--font-display);
   748  font-size: 1.25rem;
   749  font-weight: 600;
   750  color: var(--color-espresso);
   751  margin-bottom: var(--space-2);
   752}
   753
   754.modal-message {
   755  font-size: 0.9375rem;
   756  color: var(--color-warm-gray);
   757  margin-bottom: var(--space-6);
   758  line-height: 1.5;
   759}
   760
   761.modal-actions {
   762  display: flex;
   763  gap: var(--space-3);
   764}
   765
   766/* ============================================
   767   FAB (Floating Action Button)
   768   ============================================ */
   769
   770.fab {
   771  position: fixed;
   772  bottom: var(--space-6);
   773  right: var(--space-6);
   774  width: 64px;
   775  height: 64px;
   776  border-radius: var(--radius-full);
   777  background: var(--color-terracotta);
   778  color: var(--color-white);
   779  border: none;
   780  box-shadow: var(--shadow-lg);
   781  cursor: pointer;
   782  display: flex;
   783  align-items: center;
   784  justify-content: center;
   785  transition: all var(--duration-fast);
   786}
   787
   788.fab:hover {
   789  background: var(--color-terracotta-light);
   790  transform: scale(1.08);
   791  box-shadow: var(--shadow-glow);
   792}
   793
