:root {
  /* Primary - Teal */
  --primary-50: #ecfeff;
  --primary-100: #cffafe;
  --primary-200: #a5f3fc;
  --primary-400: #22d3ee;
  --primary-500: #06b6d4;
  --primary-600: #0891b2;
  --primary-700: #0e7490;

  /* Accent - Coral */
  --accent-400: #fb923c;
  --accent-500: #f97316;
  --accent-600: #ea580c;

  /* Neutrals - Warm tinted */
  --white: #ffffff;
  --gray-50: #fafaf9;
  --gray-100: #f5f5f4;
  --gray-200: #e7e5e4;
  --gray-300: #d6d3d1;
  --gray-400: #a8a29e;
  --gray-500: #78716c;
  --gray-600: #57534e;
  --gray-700: #44403c;

  /* Danger */
  --red-500: #ef4444;
  --red-600: #dc2626;

  /* Macros */
  --color-protein: #8b5cf6;
  --color-carbs: #f59e0b;
  --color-fat: #ec4899;

  /* Typography & Layout */
  --font-family: 'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
  --border-radius: 12px;
  --border-radius-sm: 8px;

  /* Shadows - Teal tinted */
  --shadow-sm: 0 1px 2px rgba(6, 182, 212, 0.08);
  --shadow: 0 4px 6px -1px rgba(6, 182, 212, 0.1);
  --shadow-lg: 0 10px 15px -3px rgba(6, 182, 212, 0.15);
  --shadow-coral: 0 8px 24px rgba(249, 115, 22, 0.3);
}

* {
  box-sizing: border-box;
  margin: 0;
  padding: 0;
}

body {
  font-family: var(--font-family);
  background: var(--primary-50);
  color: var(--gray-700);
  line-height: 1.6;
  min-height: 100vh;
}

#app {
  max-width: 480px;
  margin: 0 auto;
  padding: 16px;
  padding-bottom: 100px;
}

/* Header */
.header {
  margin-bottom: 24px;
}

.header-top {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 16px;
}

.header-title {
  font-size: 1.5rem;
  font-weight: 600;
  color: var(--primary-700);
}

.date-nav {
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 8px;
}

.date-display {
  background: var(--white);
  border: 1px solid var(--primary-200);
  border-radius: var(--border-radius-sm);
  padding: 8px 20px;
  font-size: 1rem;
  font-weight: 500;
  color: var(--primary-700);
  cursor: pointer;
  transition: all 0.2s;
}

.date-display:hover {
  background: var(--primary-100);
  border-color: var(--primary-400);
}

.btn-icon {
  background: var(--white);
  border: 1px solid var(--primary-200);
  border-radius: var(--border-radius-sm);
  width: 40px;
  height: 40px;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  color: var(--primary-600);
  transition: all 0.2s;
}

.btn-icon:hover:not(:disabled) {
  background: var(--primary-100);
  border-color: var(--primary-400);
}

.btn-icon:disabled {
  opacity: 0.4;
  cursor: not-allowed;
}

.btn-icon:focus-visible,
.date-display:focus-visible {
  outline: 2px solid var(--primary-500);
  outline-offset: 2px;
}

/* Progress Card */
.progress-card {
  background: var(--white);
