  width: 100%;
  max-width: 340px;
  animation: scaleIn 0.2s ease;
}

@keyframes scaleIn {
  from {
    opacity: 0;
    transform: scale(0.95);
  }
  to {
    opacity: 1;
    transform: scale(1);
  }
}

.modal-title {
  font-size: 1.125rem;
  font-weight: 600;
  color: var(--gray-700);
  margin-bottom: 8px;
}

.modal-message {
  font-size: 0.9375rem;
  color: var(--gray-500);
  margin-bottom: 24px;
}

.modal-actions {
  display: flex;
  gap: 12px;
}

/* FAB */
.fab {
  position: fixed;
  bottom: 24px;
  right: 24px;
  width: 60px;
  height: 60px;
  border-radius: 50%;
  background: var(--primary-600);
  color: var(--white);
  border: none;
  box-shadow: var(--shadow-coral);
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  transition: all 0.2s;
}

.fab:hover {
  background: var(--primary-700);
  transform: scale(1.05);
}

.fab:active {
  transform: scale(0.95);
}

.fab:focus-visible {
  outline: 2px solid var(--primary-700);
  outline-offset: 2px;
}

@media (min-width: 480px) {
  .fab {
    right: calc(50% - 240px + 24px);
  }
}

/* Calendar */
.calendar-modal {
  background: var(--white);
  border-radius: var(--border-radius);
  padding: 20px;
  width: 100%;
  max-width: 320px;
  animation: scaleIn 0.2s ease;
}

.calendar-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 16px;
}

.calendar-title {
  font-size: 1rem;
  font-weight: 600;
  color: var(--primary-700);
}

.calendar-weekdays {
  display: grid;
  grid-template-columns: repeat(7, 1fr);
  gap: 4px;
  margin-bottom: 8px;
}

.calendar-weekday {
  text-align: center;
  font-size: 0.75rem;
  font-weight: 500;
  color: var(--gray-500);
  padding: 4px;
}

.calendar-days {
  display: grid;
  grid-template-columns: repeat(7, 1fr);
  gap: 4px;
}

.calendar-day {
  aspect-ratio: 1;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 0.875rem;
  border: none;
  background: none;
  border-radius: 50%;
  cursor: pointer;
  color: var(--gray-700);
  transition: all 0.2s;
}

.calendar-day:hover:not(:disabled):not(.calendar-day--empty) {
  background: var(--primary-100);
}

.calendar-day--empty {
  cursor: default;
}

.calendar-day--today {
  font-weight: 600;
  color: var(--primary-600);
  border: 2px solid var(--primary-400);
}

.calendar-day--selected {
  background: var(--primary-600);
  color: var(--white);
}

.calendar-day--selected:hover:not(:disabled) {
  background: var(--primary-700);
}

.calendar-day--disabled {
  color: var(--gray-300);
  cursor: not-allowed;
}

.calendar-today-btn {
  width: 100%;
  margin-top: 16px;
}

/* Settings Sections */
.form-section-title {
  font-size: 0.875rem;
  font-weight: 600;
  color: var(--gray-500);
  text-transform: uppercase;
  letter-spacing: 0.05em;
  margin-top: 20px;
  margin-bottom: 12px;
}

.form-section-title:first-of-type {
  margin-top: 0;
}

.data-actions {
  display: flex;
  gap: 12px;
  margin-bottom: 16px;
}

.data-actions .btn {
  flex: 1;
  font-size: 0.875rem;
  padding: 10px 16px;
}

.status-message {
  text-align: center;
  font-size: 0.875rem;
  color: var(--primary-600);
  margin-bottom: 16px;
}

/* Profile Styles */
.profile-modal {
  background: var(--white);
  border-radius: var(--border-radius);
  padding: 24px;
  width: 100%;
  max-width: 340px;
  animation: scaleIn 0.2s ease;
}

.profile-empty {
  text-align: center;
  color: var(--gray-500);
  padding: 20px 0;
}

.profile-list {
  display: flex;
  flex-direction: column;
  gap: 8px;
  margin-bottom: 16px;
}

.profile-item {
  width: 100%;
}

.profile-select-btn {
  width: 100%;
  padding: 16px;
  font-size: 1.125rem;
  font-weight: 500;
  background: var(--gray-50);
  border: 1px solid var(--gray-200);
  border-radius: var(--border-radius-sm);
  cursor: pointer;
  transition: all 0.2s;
  color: var(--gray-700);
}

.profile-select-btn:hover {
  background: var(--primary-100);
  border-color: var(--primary-400);
}

.profile-pin-entry {
  display: flex;
  align-items: center;
  gap: 8px;
  padding: 8px;
  background: var(--gray-50);
  border-radius: var(--border-radius-sm);
}

.profile-name {
  font-weight: 500;
  flex: 1;
}

.pin-input {
  width: 60px;
  padding: 8px;
  font-size: 1rem;
  text-align: center;
  border: 1px solid var(--gray-300);
  border-radius: var(--border-radius-sm);
}

.pin-input:focus {
  outline: none;
  border-color: var(--primary-500);
}

.btn--sm {
  padding: 8px 12px;
  font-size: 0.875rem;
}

.profile-add-btn {
  width: 100%;
  margin-top: 8px;
}

.profile-add-form {
  display: flex;
  flex-direction: column;
  gap: 12px;
  margin-top: 16px;
  padding-top: 16px;
  border-top: 1px solid var(--gray-200);
}

.error-message {
  color: var(--red-500);
  font-size: 0.875rem;
  text-align: center;
  margin-top: 8px;
}

.profile-management {
  margin-bottom: 20px;
}

.current-profile-name {
  font-size: 1.25rem;
  font-weight: 600;
  color: var(--primary-700);
  margin-bottom: 12px;
}

.profile-actions {
  display: flex;
  flex-wrap: wrap;
  gap: 8px;
}

.profile-actions .btn {
  flex: 1;
  min-width: 120px;
}

.delete-confirm {
  margin-top: 16px;
  padding: 16px;
  background: var(--gray-50);
  border-radius: var(--border-radius-sm);
}

.delete-confirm p {
  margin-bottom: 12px;
  color: var(--gray-600);
}

.header-profile {
  display: flex;
  align-items: center;
  gap: 8px;
}

.header-profile-name {
  font-size: 0.875rem;
  color: var(--gray-500);
}

.btn-lock {
  background: none;
  border: none;
  padding: 4px;
  cursor: pointer;
  color: var(--gray-400);
  transition: color 0.2s;
}

.btn-lock:hover {
  color: var(--primary-600);
}

.profile-hint {
  color: var(--gray-500);
  font-size: 0.875rem;
  margin-bottom: 12px;
}

