        {MEAL_TYPES.map(type => (
          <button
            type="button"
            class={`meal-type-btn ${mealType === type.id ? 'active' : ''}`}
            onClick={() => setMealType(type.id)}
          >
            <span class="meal-type-icon">{type.icon}</span>
            <span class="meal-type-label">{type.label}</span>
